catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '' || g_form.getValue('currentSet') == '') {
      return;
   }

	if(g_form.getValue('phase') == 'Modify' && newValue != g_form.getValue('subscription_name').toString()){
		control.style.backgroundColor = 'yellow';
		control.style.color = 'purple';
		console.log("Change disk 1 : "+newValue+" : "+g_form.getValue('subscription_name').toString());
		return;
	}else if(g_form.getValue('phase') == 'Modify' && newValue == g_form.getValue('subscription_name').toString()){
		control.style.backgroundColor = '#FFF';
		control.style.color = '#343D47';
		console.log("back to "+newValue+" : "+g_form.getValue('subscription_name').toString());
		return;
	}

}
